package org.nanocontainer.nanowar.nanoweb.impl;import java.util.Comparator;import org.apache.commons.collections.ComparatorUtils;/** * Comparator utilizado para ordenar os parametros enviado a action. *  * @version $Id$ */public final class ParameterComparator implements Comparator {	private Comparator naturalComparator = ComparatorUtils.naturalComparator();	public int compare(Object o1, Object o2) {		if (!((o1 instanceof String) && (o2 instanceof String))) {			return 0;		}		int result = ((String) o1).length() - ((String) o2).length();		if (result == 0) {			result = naturalComparator.compare(o1, o2);		}		return result;	}}