pico = new org.picocontainer.defaults.DefaultPicoContainer(parent)
if(assemblyScope instanceof javax.servlet.ServletContext) {
    System.out.println("**** Webapp-level pico components registered by Groovy")
} else if(assemblyScope instanceof javax.servlet.http.HttpSession) {
    System.out.println("**** Session-level pico components registered by Groovy")
} else if(assemblyScope instanceof javax.servlet.ServletRequest) {
    System.out.println("**** Request-level pico components registered by Groovy")

    // Map NanoWeb Actions
    pico.registerComponentImplementation("/demo.nano", org.nanocontainer.sample.nanoweb.DemoAction)
}
