context-param.param-value:
// No need to extend anything, but we must have a method with this signature
class DemoBuilder {
    // NanoContainer will call this method whenever:
    // o The application starts
    // o A new session is created
    // o A new request is created
    buildContainer(parent, assemblyScope) {
        pico = new org.picocontainer.defaults.DefaultPicoContainer(parent)
        if(assemblyScope instanceof javax.servlet.ServletContext) {
            // register application specific components
            System.out.println("**** NanoContainer instantiating application components")
            pico.registerComponentImplementation(org.nanocontainer.sample.webwork.dao.simple.MemoryCheeseDao)
        } else if(assemblyScope instanceof javax.servlet.ServletContext) {
        }
        return pico
    }
}
